        self.ImageSubsample = image_subsample
        self.zoom = int(image_zoom) if image_zoom is not None else None
        self.Disabled = disabled
        self.IsButtonMenu = True
        self.MenuItemChosen = None
        
        # type: tk.Menubutton
        self.Widget = self.TKButtonMenu = None

          # type: tk.Menu
        self.TKMenu = None

